#day six part One
with open('src/puzzle_6.txt', 'r') as f:
    line = f.read()

tab = []

string = ''
def char_not_in_previous(string, index):
    if index <= 4:
        print("MINCE")
        return False
    elif index > 4 :
        if len(set(string[index-4:index])) == 4:
            print("=================================")
            return True

for a, char in enumerate(string):
    tab.append(char)
    if char_not_in_previous(tab, a):
        break

print(a)

